package StarProblem;

public class Number_Pattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=4;
		for(int i=1;i<=n;i++) // Row printing.
		{
			for(int j=0;j<=n;j++) // Column printing.
			{
				System.out.print(i);
			}
			System.out.println(" ");
		}

	}

}
