package com.Saleem.Strings;

public class StringBuilderAndBuffers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		StringBuffer s1=new StringBuffer("Om");
		s1.append("kar");
		System.out.println(s1);
		StringBuilder s=new StringBuilder("Saleem Ur ");
		s.append("Rahman");
		System.out.println(s);
		

	}

}
